import React, { useState } from "react";

export default function AnswerCard({ prompt, answer, onDelete }) {
  const [expanded, setExpanded] = useState(false);

  const expandCard = () => {
    setExpanded(true);
  };

  const closeExpandedCard = () => {
    setExpanded(false);
  };

  return (
    <div className = "font-loader">
      {!expanded && (
        <div
          className="h-60 bg-slate-100 border border-slate-300 grid grid-col-2 p-4 gap-4 rounded-lg shadow-md transition-all duration-300 transform hover:scale-105
          "
          onClick={expandCard}
          style={{ cursor: 'pointer' }} 
        >
          <div className="col-span-2 text-lg capitalize rounded-md">
            {prompt}
          </div>
          <div className="col-span-1 flex items-end">
            <button
              onClick={onDelete}
              className="rounded-md bg-slate-300 hover:bg-slate-600 hover:text-slate-200 duration-300 p-2"
            >
              Delete
            </button>
          </div>
        </div>
      )}

      {expanded && (
        <div className="expanded transition-all duration-300 flex flex-col">
          <div className="text-lg font-loader capitalize rounded-md ">
            {prompt}
          </div>
          <div className="card-answer"> {answer}</div>
          <div className="flex-grow"></div> 
          <div className="flex items-end">
            <button
              className="close-button rounded-md bg-slate-300 hover:bg-slate-600 hover:text-slate-200 duration-300 p-2"
              onClick={closeExpandedCard}
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
}
