import React from "react";

export default function AnswerCard({ prompt, answer, onDelete }) {
  return (
    <div className="w-full sm:w-1/2 md:w-1/3 lg:w-1/4 xl:w-1/5 p-2">
      <div className="w-full h-full bg-gradient-to-l from-slate-300 to-slate-100 text-slate-600 border border-slate-300 grid grid-col-2 p-4 gap-4 rounded-lg shadow-md hover: bg-stone-950">
        <div className="col-span-2 text-lg font-bold capitalize rounded-md">
          {prompt}
        </div>
        <div className="col-span-2 rounded-md flex items-end"> {answer} </div>
        <div className="col-span-1 flex items-end">
          <button
            onClick={onDelete}
            className="rounded-md bg-slate-300 hover:bg-slate-600 hover:text-slate-200 duration-300 p-2"
          >
            Delete Card
          </button>
        </div>
      </div>
    </div>
  );
}
